// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import NewsFeed from './components/NewsFeed';
import PositionForm from './components/PositionForm';
import PositionsList from './components/PositionsList';
import ApplicationForm from './components/ApplicationForm';
import './index.css'; // Our main CSS

function SideMenu({ isOpen, onClose }) {
  return (
    <div className={`side-menu ${isOpen ? 'open' : ''}`}>
      <button className="close-btn" onClick={onClose}>X</button>
      <ul>
        <li>
          <Link to="/" onClick={onClose}>
            Home
          </Link>
        </li>
        <li>
          <Link to="/positions" onClick={onClose}>
            View Positions
          </Link>
        </li>
        <li>
          <Link to="/post-position" onClick={onClose}>
            Post a Position
          </Link>
        </li>
        <li>
          <Link to="/apply" onClick={onClose}>
            Apply
          </Link>
        </li>
      </ul>
    </div>
  );
}

function App() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <Router>
      <div>
        {/* Header with Hamburger Icon and Title */}
        <header className="header">
          <div className="menu-icon" onClick={toggleMenu}>
            <div className="bar"></div>
            <div className="bar"></div>
            <div className="bar"></div>
          </div>
          <div className="header-title">AcademiNet</div>
        </header>

        {/* Side Menu (Drawer) */}
        <SideMenu isOpen={isMenuOpen} onClose={toggleMenu} />

        <div className="container">
          <Routes>
            <Route path="/post-position" element={<PositionForm />} />
            <Route path="/positions" element={<PositionsList />} />
            <Route path="/apply" element={<ApplicationForm />} />
            {/* Default Route to NewsFeed or whichever component you want */}
            <Route path="/" element={<NewsFeed />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
