// src/components/Signup.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Signup.css';

function Signup() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');

  // Client-side password validation
  function validatePassword(pwd) {
    const re = /^(?=.*[0-9])(?=.*[!@#$%^&*])/;
    return pwd.length >= 8 && re.test(pwd);
  }

  const handleSignup = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setMessage("Confirmed password is mispelled. Passwords do not match.");
      return;
    }
    if (!validatePassword(password)) {
      setMessage("Password must be at least 8 characters long and contain at least one number and one special character.");
      return;
    }
    if (!email.endsWith('.edu')) {
      setMessage("Email must end in .edu");
      return;
    }
    try {
      const res = await axios.post('http://localhost:5000/api/auth/signup', { name, email, password, confirmPassword });
      setMessage(res.data.message + ". Your account ID is " + res.data.accountId);
    } catch (err) {
      setMessage(err.response?.data.error || 'An error occurred');
    }
  };

  return (
    <div className="signup-page">
      <h2>Sign Up</h2>
      <p>Password must be at least 8 characters long and contain at least one number and one special character.</p>
      <form onSubmit={handleSignup} className="signup-form">
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Email (.edu):</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Confirm Password:</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Sign Up</button>
      </form>
      <p>
        Already have an account? <Link to="/login">Login here</Link>
      </p>
      {message && <p>{message}</p>}
    </div>
  );
}

export default Signup;
