// frontend/src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import PositionForm from './components/PositionForm';
import PositionsList from './components/PositionsList';
import ApplicationForm from './components/ApplicationForm';

function App() {
  return (
    <Router>
      <div className="container">
        <h1>Research Positions Portal</h1>
        <nav>
          <ul>
            <li><Link to="/positions">View Positions</Link></li>
            <li><Link to="/post-position">Post a Position (Researcher)</Link></li>
            <li><Link to="/apply">Apply (Student)</Link></li>
          </ul>
        </nav>
        <Switch>
          <Route path="/post-position" component={PositionForm} />
          <Route path="/positions" component={PositionsList} />
          <Route path="/apply" component={ApplicationForm} />
          <Route path="/" exact component={PositionsList} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
